import React from "react";
import { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import { instance } from "../axios/axiosInstance";
import CardContent from "@mui/material/CardContent";
import Grid from "@mui/material/Grid";
import Stack from "@mui/material/Stack";
import LinearProgress, {
  LinearProgressProps,
} from "@mui/material/LinearProgress";
import Alert from "@mui/material/Alert";
import IconButton from "@mui/material/IconButton";
import CloseIcon from "@mui/icons-material/Close";
import Collapse from "@mui/material/Collapse";

// const steps = [
//   "Question 1",
//   "Question 2",
//   "Question 3",
//   "Question 4",
//   "Question 5",
//   "Question 6",
//   "Question 7",
//   "Question 8",
//   "Question 9",
//   "Question 10",
// ];
// yourArray.sort(function() { return 0.5 - Math.random() });
const Practice = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [skipped, setSkipped] = useState(new Set());
  const [words, setWords] = useState([]);
  const [word, setWord] = useState("");
  const [options, setOptions] = useState([]);
  const [progress, setProgress] = React.useState(0);
  const [correctAnswers, setCorrectAnswers] = React.useState(0);
  const [open, setOpen] = React.useState(false);

  function getWords() {
    instance.get("words").then((res) => {
      console.log(res.data);
      setWords(res.data);
    });
  }
  useEffect(() => {
    getWords();
    setOptions(
      ["verb", "adjective", "noun", "adverb"].sort(function () {
        return 0.5 - Math.random();
      })
    );
  }, []);

  useEffect(() => {
    if (words) {
      setWord(words[0]);
    }
  }, [words]);

  useEffect(() => {
    if (correctAnswers) {
      setProgress((prevProgress) =>
        prevProgress >= 100 ? 0 : (correctAnswers / 10) * 100
      );
    }
  }, [correctAnswers]);

  const isStepOptional = (step) => {
    return false;
  };

  const handleAnswer = (option) => {
    if (word.pos == option) {
      alert("true");
      setCorrectAnswers(correctAnswers + 1);
    } else {
      alert("false");
    }
    setOpen(true);
    setTimeout(() => {
      setOpen(false);
      handleNext();
    }, 2000);

    console.log(word, option);
  };
  const isStepSkipped = (step) => {
    return skipped.has(step);
  };

  const handleNext = () => {
    let newSkipped = skipped;
    // if (isStepSkipped(activeStep)) {
    //   newSkipped = new Set(newSkipped.values());
    //   newSkipped.delete(activeStep);
    // }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped(newSkipped);
    setWord(words[activeStep + 1]);
    setOptions(
      ["verb", "adjective", "noun", "adverb"].sort(function () {
        return 0.5 - Math.random();
      })
    );
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleSkip = () => {
    if (!isStepOptional(activeStep)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    setSkipped((prevSkipped) => {
      const newSkipped = new Set(prevSkipped.values());
      newSkipped.add(activeStep);
      return newSkipped;
    });
  };

  const handleReset = () => {
    setActiveStep(0);
    getWords();
    setCorrectAnswers(0);
    setProgress(0);
  };
  function LinearProgressWithLabel(props) {
    return (
      <Box sx={{ display: "flex", alignItems: "center" }}>
        <Box sx={{ width: "100%", mr: 1 }}>
          <LinearProgress variant="determinate" {...props} />
        </Box>
        <Box sx={{ minWidth: 35 }}>
          <Typography variant="body2" color="text.secondary">{`${Math.round(
            props.value
          )}%`}</Typography>
        </Box>
      </Box>
    );
  }
  return (
    <Box sx={{ width: "90%", margin: "auto" }}>
      <Stepper activeStep={activeStep} className="mt-5 mx-5">
        {words.map((word, index) => {
          const stepProps = {};
          const labelProps = {};
          if (isStepOptional(index)) {
            labelProps.optional = (
              <Typography variant="caption">Optional</Typography>
            );
          }
          if (isStepSkipped(index)) {
            stepProps.completed = false;
          }
          return (
            <Step key={index} {...stepProps}>
              <StepLabel {...labelProps}>Question {index + 1}</StepLabel>
            </Step>
          );
        })}
      </Stepper>
      {activeStep === words.length ? (
        <React.Fragment>
          <Typography sx={{ mt: 2, mb: 1 }} className="my-5 font-weight-bold fs-1 text-center">
            All steps completed - you&apos;re finished
          </Typography>
          <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
            <Box sx={{ flex: "1 1 auto" }} />
            <Button onClick={handleReset}>Reset</Button>
          </Box>
        </React.Fragment>
      ) : (
        <React.Fragment>
          <Grid container spacing={2}>
            <Grid>
              <CardContent className="mx-5 my-5">
                <Typography variant="h5" component="div" className="fs-1">
                  {word ? word.word : ""}
                </Typography>
              </CardContent>
              <Stack spacing={5} direction="row" className="mx-5 ">
                {options.map((option, index) => {
                  return (
                    <>
                      <Button
                        key={index}
                        variant="contained"
                        onClick={() => {
                          handleAnswer(option);
                        }}
                      >
                        {option}
                      </Button>
                    </>
                  );
                })}
                {/* <Button variant="contained">Contained</Button>
                <Button variant="contained">Contained</Button>
                <Button variant="contained">Contained</Button> */}
              </Stack>
            </Grid>
          </Grid>

          {/* <Typography sx={{ mt: 2, mb: 1 }}>Step {activeStep + 1}</Typography> */}
          <Box sx={{ display: "flex", flexDirection: "row", pt: 2 }}>
            {/* <Button
              color="inherit"
              disabled={activeStep === 0}
              onClick={handleBack}
              sx={{ mr: 1 }}
            >
              Back
            </Button> */}
            <Box sx={{ flex: "1 1 auto" }} />
            <Button onClick={handleNext} className="fs-1">
              {activeStep === words.length - 1 ? "Finish" : "Next"}
            </Button>
          </Box>
        </React.Fragment>
      )}
      <Collapse className="w-30" in={open}>
        <Alert sx={{ mb: 2 }}>Correct</Alert>
      </Collapse>
      <Box sx={{ width: "40%", margin: "auto" }}>
        <LinearProgressWithLabel value={progress} className="mt-5" />
      </Box>
    </Box>
  );
};

export default Practice;
